import React from 'react';

function UserForm(props) {
    const {setName, setEmail, setRole} = props
   function handleClick(e) {
    e.preventDefault();
    }
    return (
        <div>
            <h2>Add Team Member</h2>
            <form>
            <label htmlFor='fnameInput'>
               <input id='fnameInput' type='text' placeholder='Enter Name' name='fname' onChange={(event) => setName(event.target.value)}></input> 
            </label>
            <label htmlFor='emailInput'>
               <input htmlFor='emailInput' type='email' placeholder='Enter email' name='email' onChange={(event) => setEmail}></input> 
            </label>
            <label htmlFor='roleSelect'>
               <select id='roleSelect'>
                    <option value='1'>Tank</option>
                    <option value='2'>Main DPS</option>   
                    <option value='3'>Back-up DPS</option>   
                    <option value='4'>Healer</option>      
                </select> 
                <input type='Submit' onClick={handleClick}></input>
            </label>

            </form>
        </div>
    )
}
export default UserForm;
